import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminPage = () => {
  const [totalAmount, setTotalAmount] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [cards, setCards] = useState([]);
  const [accounts, setAccounts] = useState([]);
  
  useEffect(() => {
    fetchTotalAmount();
    fetchAllTransactions();
    fetchAllCards();
    fetchAllAccounts();
  }, []);

  const fetchTotalAmount = async () => {
    const response = await axios.get('/api/admin/total-amount');
    setTotalAmount(response.data.totalAmount);
  };

  const fetchAllTransactions = async () => {
    const response = await axios.get('/api/admin/transactions');
    setTransactions(response.data.transactions);
  };

  const fetchAllCards = async () => {
    const response = await axios.get('/api/admin/cards');
    setCards(response.data.cards);
  };

  const fetchAllAccounts = async () => {
    const response = await axios.get('/api/admin/accounts');
    setAccounts(response.data.accounts);
  };

  const handleDeleteAccount = async (accountId) => {
    await axios.delete(`/api/admin/accounts/${accountId}`);
    fetchAllAccounts(); // Refresh the accounts list
  };

  const handleMakePayment = async (transactionData) => {
    await axios.post('/api/admin/make-payment', transactionData);
    fetchAllTransactions(); // Refresh transactions
    fetchTotalAmount(); // Refresh total amount
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      
      <section>
        <h2>Total Amount in Bank: ${totalAmount}</h2>
      </section>
      
      <section>
        <h2>All Transactions</h2>
        <table>
          <thead>
            <tr>
              <th>From Account</th>
              <th>To Account</th>
              <th>Amount</th>
              <th>Status</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((transaction) => (
              <tr key={transaction.transactionId}>
                <td>{transaction.fromAccountId}</td>
                <td>{transaction.toAccountId}</td>
                <td>${transaction.amount}</td>
                <td>{transaction.status}</td>
                <td>{new Date(transaction.time).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
      
      <section>
        <h2>All Cards</h2>
        <table>
          <thead>
            <tr>
              <th>Card ID</th>
              <th>User ID</th>
              <th>Account ID</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {cards.map((card) => (
              <tr key={card.cardId}>
                <td>{card.cardId}</td>
                <td>{card.userId}</td>
                <td>{card.accountId}</td>
                <td>{card.status ? 'Active' : 'Inactive'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
      
      <section>
        <h2>Manage Accounts</h2>
        <table>
          <thead>
            <tr>
              <th>Account ID</th>
              <th>User ID</th>
              <th>Balance</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {accounts.map((account) => (
              <tr key={account.accountId}>
                <td>{account.accountId}</td>
                <td>{account.userId}</td>
                <td>${account.balance}</td>
                <td>
                  <button onClick={() => handleDeleteAccount(account.accountId)}>Delete Account</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
      
      <section>
        <h2>Make Payment</h2>
        {/* Add a form to make payments */}
        <form onSubmit={(e) => {
          e.preventDefault();
          const formData = new FormData(e.target);
          const transactionData = {
            fromAccountId: formData.get('fromAccountId'),
            toAccountId: formData.get('toAccountId'),
            amount: parseFloat(formData.get('amount')),
            mode: formData.get('mode'),
            remark: formData.get('remark'),
            cardId: formData.get('cardId') || null, // Optional for card payments
          };
          handleMakePayment(transactionData);
        }}>
          <input type="number" name="fromAccountId" placeholder="From Account ID" required />
          <input type="number" name="toAccountId" placeholder="To Account ID" required />
          <input type="number" name="amount" placeholder="Amount" required />
          <input type="text" name="mode" placeholder="Mode (CASH/CARD/NETBANKING)" required />
          <input type="text" name="remark" placeholder="Remark" />
          <input type="number" name="cardId" placeholder="Card ID (Optional for Card Transactions)" />
          <button type="submit">Make Payment</button>
        </form>
      </section>
    </div>
  );
};

export default AdminPage;
