import React, { useState, useEffect } from 'react';
import './slider.css';

const testimonialsData = [
  {
    name: "John Doe",
    text: "The bank's services have been amazing. Fast and reliable.",
    image: "https://randomuser.me/api/portraits/men/1.jpg",
  },
  {
    name: "Jane Smith",
    text: "Excellent customer support. They helped me in no time.",
    image: "https://randomuser.me/api/portraits/women/2.jpg",
  },
  {
    name: "Robert Johnson",
    text: "Their financial advice helped me make better investments.",
    image: "https://randomuser.me/api/portraits/men/3.jpg",
  },
  {
    name: "Emily Davis",
    text: "I love the flexibility in account management. Great experience!",
    image: "https://randomuser.me/api/portraits/women/4.jpg",
  },
];

const TestimonialsSlider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex + 2 >= testimonialsData.length ? 0 : prevIndex + 2
      );
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="testimonial-slider">
      <div className="testimonial-container">
        {testimonialsData.slice(currentIndex, currentIndex + 2).map((testimonial, index) => (
          <div key={index} className="testimonial-card">
            <img
              src={testimonial.image}
              alt={testimonial.name}
              className="testimonial-image"
            />
            <div className="testimonial-content">
              <p className="testimonial-text">{testimonial.text}</p>
              <h3 className="testimonial-name">{testimonial.name}</h3>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TestimonialsSlider;
