import React, { useState, useEffect } from "react";
import "./Transactions_all.css";

function TransactionDisplayAll() {
  const [transactions, setTransactions] = useState([]);
  const [expandedTransactionId, setExpandedTransactionId] = useState(null);
  const [selectedAccountId, setSelectedAccountId] = useState(null); // Track the selected account
  const [userDetails, setUserDetails] = useState(null); // Track user details

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await fetch(
          `http://localhost:7000/api/payment.ginkou.in/admin/all`
        );
        const data = await response.json();

        const combinedTransactions = [
          ...data.sucessTransactions.map((transaction) => ({
            ...transaction,
            type: "success",
          })),
          ...data.failedTransactions.map((transaction) => ({
            ...transaction,
            type: "failed",
          })),
        ].sort((a, b) => new Date(b.time) - new Date(a.time));

        setTransactions(combinedTransactions);
      } catch (error) {
        console.error("Error fetching transactions:", error);
      }
    };

    fetchTransactions();
  }, []);

  const handleDetailsClick = async (userId, accountId) => {
    try {
      const response = await fetch(
        `http://localhost:7000/api/getUser/${userId}`
      );
      const data = await response.json();
      setUserDetails(data);
      setSelectedAccountId(accountId); // Set the selected account ID
    } catch (error) {
      console.error("Error fetching user details:", error);
    }
  };

  const toggleDetails = (transactionId) => {
    setExpandedTransactionId((prevId) =>
      prevId === transactionId ? null : transactionId
    );
  };

  const getTransactionStyle = (transaction) => {
    if (transaction.type === "failed") {
      return { color: "red" };
    } else if (transaction.type === "success") {
      return { color: "blue" };
    }
    return {};
  };

  return (
    <div className="transaction-container">
      {transactions.length === 0 ? (
        <div style={{ textAlign: "center", padding: "20px" }}>
          <img
            src="https://img.freepik.com/free-vector/hand-drawn-no-data-illustration_23-2150696467.jpg?t=st=1727115903~exp=1727119503~hmac=84814b7f18a43e09d89421208d9572e2835b0c7a0338e4a22970fb770e6cfadf&w=740"
            alt="No Transactions"
            style={{ width: "100%", height: "100%" }}
          />
          <p
            style={{
              fontSize: "20px",
              color: "rgb(245, 142, 187)",
              fontWeight: "bold",
            }}
          >
            -- No data available --
          </p>
        </div>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Mode</th>
              <th>Type</th>
              <th>Amount</th>
              <th>Info</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((transaction) => (
              <React.Fragment
                key={`${transaction.transactionId}-${transaction.type}`}
              >
                <tr
                  style={getTransactionStyle(transaction)}
                  onClick={() => toggleDetails(transaction.transactionId)}
                >
                  <td>{new Date(transaction.time).toLocaleDateString()}</td>
                  <td className="transaction-type">{transaction.mode}</td>
                  <td>{transaction.type}</td>
                  <td>
                    {transaction.amount.toLocaleString(undefined, {
                      style: "currency",
                      currency: "INR",
                    })}
                  </td>
                  <td>
                    <span
                      role="img"
                      aria-label="details"
                      style={{
                        marginLeft: "5px",
                        cursor: "pointer",
                        fontSize: "20px",
                      }}
                      onClick={() => handleDetailsClick(transaction.userId, transaction.fromAccountId)}
                    >
                      ℹ️
                    </span>
                  </td>
                </tr>
                {expandedTransactionId === transaction.transactionId && (
                  <tr className="transaction-details">
                    <td colSpan="5">
                      <div>
                        <strong>From Account:</strong>{" "}
                        {transaction.fromAccountId} <br />
                        <strong>To Account:</strong> {transaction.toAccountId}{" "}
                        <br />
                        <strong>Transcript:</strong> {transaction.remark}
                      </div>
                      {selectedAccountId === transaction.fromAccountId && userDetails && (
                        <div className="user-details">
                          <div className="user-details-header">
                            <p>
                              <strong>Name:</strong> {userDetails.firstName}{" "}
                              {userDetails.lastName}
                            </p>
                            <button
                              onClick={() => setSelectedAccountId(null)}
                              style={{ fontWeight: "bold", alignSelf: "flex-end" }}
                            >
                              X
                            </button>
                          </div>
                          <p>
                            <strong>Email:</strong> {userDetails.email}
                          </p>
                          <p>
                            <strong>Date of Birth:</strong>{" "}
                            {new Date(userDetails.DOB).toLocaleDateString()}
                          </p>
                        </div>
                      )}
                    </td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default TransactionDisplayAll;
