import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./transaction.css";

function TransactionDisplay() {
  const { id } = useParams();
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    if (id) {
      fetch(`http://localhost:7000/api/payment.ginkou.in/allTransaction/${id}`)
        .then((response) => response.json())
        .then((data) => setTransactions(data))
        .catch((error) => console.error("Error fetching transactions:", error));
    }
  }, [id]);

  return (
    <div className="transaction-container">
      <div className="transaction-header">
        <span>Date</span>
        <span>Type</span>
        <span>Amount</span>
      </div>
      {transactions.map((transaction) => (
        <div
          className={`transaction-item ${
            transaction.amount > 0 ? "credit" : "debit"
          }`}
          key={transaction.transactionId}
        >
          <div>
            <div className="transaction-date">
              {new Date(transaction.time).toLocaleDateString()}
            </div>
            <div className="transaction-remark">{transaction.remark}</div>
          </div>
          <div className="transaction-type">{transaction.mode}</div>
          <div>
            {transaction.amount.toLocaleString(undefined, {
              style: "currency",
              currency: "INR",
            })}
          </div>
        </div>
      ))}
    </div>
  );
}

export default TransactionDisplay;
