import React, { useState, useEffect } from 'react';
import './transaction.css';

function TransactionDisplay() {
  const [transactions, setTransactions] = useState([]);
  const userId = 3; // Assume this is the current user ID

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await fetch(`http://localhost:7000/api/payment.ginkou.in/allTransaction/${userId}`);
        const data = await response.json();
        
        // Combine credit and debit transactions and sort by time
        const combinedTransactions = [
          ...data.creditTransactions.map(transaction => ({ ...transaction, type: 'credit' })),
          ...data.debitTransactions.map(transaction => ({ ...transaction, type: 'debit' }))
        ].sort((a, b) => new Date(b.time) - new Date(a.time)); // Sort by time descending

        setTransactions(combinedTransactions);
      } catch (error) {
        console.error('Error fetching transactions:', error);
      }
    };

    fetchTransactions();
  }, [userId]);

  return (
    <div className="transaction-container">
      <div className="transaction-header">
        <span>Date</span>
        <span>Type</span>
        <span>Amount</span>
      </div>
      {transactions.map((transaction) => (
        <div className={`transaction-item ${transaction.type}`} key={transaction.transactionId}>
          <div>
            <div className="transaction-date">
              {new Date(transaction.time).toLocaleDateString()}
            </div>
            <div className="transaction-remark">{transaction.remark}</div>
          </div>
          <div className="transaction-type">{transaction.mode}</div>
          <div>
            {transaction.amount.toLocaleString(undefined, { style: 'currency', currency: 'INR' })}
          </div>
        </div>
      ))}
    </div>
  );
}

export default TransactionDisplay;
