import React, { useState } from "react";
import "./header.css";
import { Link, useNavigate } from "react-router-dom";

export default function Header() {
  const navigate = useNavigate();
  const [loggedIn, setLoggedIn] = useState(false);
  const [isUser, setIsUser] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [userId, setUserId] = useState(0);

  // Function to build links dynamically
  const getUserPath = (path) => {
    return path.replace(":userId", userId);
  };

  const handleLogout = (e) => {
    e.preventDefault();
    setLoggedIn(false);
    setIsAdmin(false);
    setIsUser(false);
    setUserId(0);
    navigate("/");
  };

  return (
    <div className="main">
      <div className="main-container">
        <h2>銀行</h2>
        <div className="links">
          |{" "}
          <Link className="link" to={`/${userId}`}>
            {" "}
            Home{" "}
          </Link>{" "}
          |
          <Link className="link" to="/">
            {" "}
            Explore{" "}
          </Link>{" "}
          |
          {isAdmin ? (
            <>
              <Link className="link" to="/manage/users">
                {" "}
                Users{" "}
              </Link>{" "}
              |
              <Link className="link" to="/manage/transactions">
                {" "}
                Transactions{" "}
              </Link>{" "}
              |
              <Link className="link" to="/manage/accounts">
                {" "}
                Accounts{" "}
              </Link>{" "}
              |
            </>
          ) : (
            <>
              <Link className="link" to={getUserPath("/accounts/:userId")}>
                {" "}
                Accounts{" "}
              </Link>{" "}
              |
              <Link className="link" to={getUserPath("/history/:userId")}>
                {" "}
                History{" "}
              </Link>{" "}
              |
              <Link className="link" to={getUserPath("/cards/:userId")}>
                {" "}
                Cards{" "}
              </Link>{" "}
              |
            </>
          )}
          {loggedIn ? (
            <Link className="link" to="/" onClick={handleLogout}>
              {" "}
              Logout |
            </Link>
          ) : (
            <Link className="link" to="/login">
              {" "}
              Login/Register |
            </Link>
          )}
        </div>
      </div>
      <marquee className="marquee-block">
        {" "}
        ನಿಮ್ಮ ಖಾತೆಯ ಬ್ಯಾಲೆನ್ಸ್ ಯಾವುದೇ ಸಮಯದಲ್ಲಿ, ಎಲ್ಲೆಲ್ಲಿ ನೋಡಿ! 24/7 ನಾವು ನಿಮ್ಮ
        ನೆರವಿಗೆ ಇರುತ್ತೇವೆ! ಇಂದು ನಮ್ಮ ಡಿಜಿಟಲ್ ಬ್ಯಾಂಕಿಂಗ್ ಕ್ರಾಂತಿಯಲ್ಲಿ ಸೇರಿ!{" "}
      </marquee>
    </div>
  );
}
