import React, { useState, useEffect } from 'react';
import './TestimonialsSlider.css';

const testimonialsData = [
  {
    name: "John Doe",
    text: "This product has changed my life! Highly recommended.",
    image: "https://randomuser.me/api/portraits/men/1.jpg",
  },
  {
    name: "Jane Smith",
    text: "Amazing service and support. I am very happy with the results.",
    image: "https://randomuser.me/api/portraits/women/2.jpg",
  },
  {
    name: "Robert Johnson",
    text: "Fantastic experience, will definitely come back again.",
    image: "https://randomuser.me/api/portraits/men/3.jpg",
  },
  {
    name: "Emily Davis",
    text: "Quick and reliable service. Five stars all the way.",
    image: "https://randomuser.me/api/portraits/women/4.jpg",
  },
];

const TestimonialsSlider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Change the testimonial every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === testimonialsData.length - 1 ? 0 : prevIndex + 1
      );
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="testimonial-slider">
      <div className="testimonial">
        <img
          src={testimonialsData[currentIndex].image}
          alt={testimonialsData[currentIndex].name}
          className="testimonial-image"
        />
        <p className="testimonial-text">{testimonialsData[currentIndex].text}</p>
        <h3 className="testimonial-name">{testimonialsData[currentIndex].name}</h3>
      </div>
    </div>
  );
};

export default TestimonialsSlider;
