import { useState } from "react";
import "./login.css";
export default function LoginComponent() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [sucess, setSucess] = useState("");

  const loginUser = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSucess(null);
    try {
      const response = await fetch("http://localhost:7000/api/user-login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          firstName,
          lastName,
          password,
        }),
      });

      if (!response.ok) {
        console.log(response.js)
        throw new Error("Invalid user credentials");
      }

      const data = response.json();
      console.log("Sucess : ", data);
      setSucess("Login Sucessfull!!");
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };
  return (
    <div class="login-main">
      <form class="login-form" onSubmit={loginUser}>
        <span>銀行</span>
        <label htmlFor="firstName">First Name </label>
        <input
          type="text"
          id="firstName"
          className="login-input"
          placeholder="John"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          required
        />
        <br />
        <label htmlFor="lastName">Last Name </label>
        <input
          type="text"
          id="lastName"
          className="login-input"
          placeholder="Doe"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          required
        />
        <br />
        <label htmlFor="password">Password </label>
        <input
          type="password"
          id="password"
          className="login-input"
          placeholder="****"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          maxLength="12"
          minLength="8"
          required
        />
        <br />
        {error && <p>Error: {error}</p>}
        {sucess && <p>{sucess}</p>}
        <button type="submit" disabled={loading} className="submit-button">
          {loading ? "Loading" : "Login"}
        </button>
      </form>
      <img src="https://img.freepik.com/free-vector/data-protection-with-biometric-identification-fingerprint-security-system-smartphone-secure-authentication-transactions-flat-woman-scans-her-face-access-data-id-scanning-technology_88138-658.jpg?t=st=1726335439~exp=1726339039~hmac=5ada4074763140572f5a83808a8c8479705dbb42e253572c9f31bfd164594db8&w=1060" alt="login-with-ginkou"></img>
    </div>
  );
}
