import React, { useState, useEffect } from 'react';

function CardDisplay() {
  const [cards, setCards] = useState([]);

  useEffect(() => {
    fetch('http://localhost:7000/api/allCards/2')
      .then(response => response.json())
      .then(data => setCards(data))
      .catch(error => console.error('Error fetching cards:', error));
  }, []);

  return (
    <div className="card-container">
      {cards.map((card, index) => (
        <div className="card" key={card.cardId}>
          <div className="card-logo">ðŸ’³</div> {/* Logo for the chip */}
          <div className="bank-name">éŠ€è¡Œ</div>
          <div className="card-number-group">
            <div className="card-number">
              {card.cardNumber.match(/.{1,4}/g).join(' ')} {/* Formats card number in groups of 4 */}
            </div>
          </div>
          <div className="valid-thru-label">Valid Thru</div>
          <div className="expiration">
            {new Date(card.expiration).toLocaleDateString('en-GB', { year: '2-digit', month: '2-digit' })}
          </div>
          <div className="cvv">CVV: {card.cvv}</div>
          <div className="cardholder">{card.typ}</div>
        </div>
      ))}
    </div>
  );
}

export default CardDisplay;
