import React, { useState, useEffect } from "react";
import "./accounts.css";

function Accounts() {
  const [accounts, setAccounts] = useState([]);
  const [userDetails, setUserDetails] = useState(null);
  const [activeAccount, setActiveAccount] = useState(null);

  useEffect(() => {
    const fetchAccounts = async () => {
      try {
        const response = await fetch("http://localhost:7000/api/getAllAccounts");
        const data = await response.json();
        setAccounts(data);
      } catch (error) {
        console.error("Error fetching accounts:", error);
      }
    };

    fetchAccounts();
  }, []);

  const handleDetailsClick = async (account) => {
    if (activeAccount === account.accountNo) {
      setActiveAccount(null); // Toggle dropdown off
      setUserDetails(null);
    } else {
      try {
        const response = await fetch(`http://localhost:7000/api/getUser/${account.userId}`);
        const data = await response.json();
        setUserDetails(data);
        setActiveAccount(account.accountNo); // Set active account
      } catch (error) {
        console.error("Error fetching user details:", error);
      }
    }
  };

  return (
    <div className="accounts-container">
      <h2>All Accounts</h2>
      <div className="accounts-horizontal-bar">
        {accounts.map((account) => (
          <div className="account-card" key={account.accountId}>
            <p><strong>Account No:</strong> {account.accountNo}</p>
            <p><strong>Balance:</strong> {account.balance}</p>
            <button className="details-btn" onClick={() => handleDetailsClick(account)}>
              Details <i className={`icon ${activeAccount === account.accountNo ? "up" : "down"}`}></i>
            </button>

            {activeAccount === account.accountNo && userDetails && (
              <div className="user-details-dropdown">
                <br />
                <h3>User Details</h3>
                <p><strong>Name:</strong> {userDetails.name}</p>
                <p><strong>Email:</strong> {userDetails.email}</p>
                <p><strong>Phone:</strong> {userDetails.phone}</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Accounts;
