import React, { useState, useEffect } from 'react';
import './transaction.css'

function TransactionDisplay() {
  const [transactions, setTransactions] = useState([]);
  const [accounts, setAccounts] = useState([]);
  const userId = 2; // Assume this is the current user ID

  // Fetch transactions for the user
  useEffect(() => {
    fetch('http://localhost:7000/api/payment.ginkou.in/allTransaction/3')
      .then(response => response.json())
      .then(data => setTransactions(data))
      .catch(error => console.error('Error fetching transactions:', error));
  }, []);

  // Fetch accounts for the user
  useEffect(() => {
    fetch(`http://localhost:7000/api/getAll/${userId}`) // Get all accounts for the current user
      .then(response => response.json())
      .then(data => setAccounts(data))
      .catch(error => console.error('Error fetching accounts:', error));
  }, [userId]);

  const isCredit = (transaction) => {
    return accounts.some(account => account.accountId === transaction.toAccountId);
  };

  const isDebit = (transaction) => {
    return accounts.some(account => account.accountId === transaction.fromAccountId);
  };

  return (
    <div className="transaction-container">
      <div className="transaction-header">
        <span>Date</span>
        <span>Type</span>
        <span>Amount</span>
      </div>
      {transactions.map((transaction) => {
        const transactionType = isCredit(transaction) ? 'credit' : isDebit(transaction) ? 'debit' : '';
        console.log(`Transaction ${transaction.transactionId} is ${transactionType}`);

        return (
          <div className={`transaction-item ${transactionType}`} key={transaction.transactionId}>
            <div>
              <div className="transaction-date">
                {new Date(transaction.time).toLocaleDateString()}
              </div>
              <div className="transaction-remark">{transaction.remark}</div>
            </div>
            <div className="transaction-type">{transaction.mode}</div>
            <div>
              {transaction.amount.toLocaleString(undefined, { style: 'currency', currency: 'INR' })}
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default TransactionDisplay;
