import React, { useState, useEffect } from 'react';
import './transaction.css';

function TransactionDisplay() {
  const [transactions, setTransactions] = useState([]);
  const userId = 2; // Assume this is the current user ID
  const [expandedTransactionId, setExpandedTransactionId] = useState(null);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await fetch(`http://localhost:7000/api/payment.ginkou.in/allTransaction/${userId}`);
        const data = await response.json();
        
        const combinedTransactions = [
          ...data.creditTransactions.map(transaction => ({ ...transaction, type: 'credit' })),
          ...data.debitTransactions.map(transaction => ({ ...transaction, type: 'debit' })),
          ...data.selfTransactions.map(transaction => ({ ...transaction, type: 'self' })),
          ...data.failedTransactions.map(transaction => ({ ...transaction, type: 'failed' }))
        ].sort((a, b) => new Date(b.time) - new Date(a.time));

        setTransactions(combinedTransactions);
      } catch (error) {
        console.error('Error fetching transactions:', error);
      }
    };

    fetchTransactions();
  }, [userId]);

  const getTransactionStyle = (transaction) => {
    if (transaction.type === 'failed') {
      return { color: 'red' };
    } else if (transaction.type === 'credit') {
      return { color: 'green' };
    } else if (transaction.type === 'debit') {
      return { color: 'blue' };
    } else if (transaction.type === 'self') {
      return { color: 'orange' };
    }
    return {};
  };

  const toggleDetails = (transactionId) => {
    setExpandedTransactionId(prevId => (prevId === transactionId ? null : transactionId));
  };

  return (
    <div className="transaction-container">
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Type</th>
            <th>Amount</th>
            <th>Details</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((transaction) => (
            <React.Fragment key={`${transaction.transactionId}-${transaction.type}`}>
              <tr style={getTransactionStyle(transaction)} onClick={() => toggleDetails(transaction.transactionId)}>
                <td className="transaction-date">
                  {new Date(transaction.time).toLocaleDateString()}
                </td>
                <td className={`transaction-type ${transaction.type}`}>
                  {transaction.mode}
                </td>
                <td>
                  {transaction.amount.toLocaleString(undefined, { style: 'currency', currency: 'INR' })}
                </td>
                <td>
                  {expandedTransactionId === transaction.transactionId ? 'Hide Details' : 'Show Details'}
                </td>
              </tr>
              {expandedTransactionId === transaction.transactionId && (
                <tr className="transaction-details">
                  <td colSpan="4">
                    <div>
                      <strong>From Account:</strong> {transaction.fromAccountId} <br />
                      <strong>To Account:</strong> {transaction.toAccountId} <br />
                      <strong>Remark:</strong> {transaction.remark}
                    </div>
                  </td>
                </tr>
              )}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TransactionDisplay;
