import React, { useState, useEffect } from 'react';

function TransactionHistory() {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    fetch('http://localhost:7000/api/payment.ginkou.in/allTransaction/3') // Fetch transactions for user with ID 3
      .then(response => response.json())
      .then(data => setTransactions(data))
      .catch(error => console.error('Error:', error));
  }, []);

  return (
    <div className="transaction-container">
      {transactions.map((transaction) => (
        <div key={transaction.transactionId} className={`transaction-item ${transaction.amount > 0 ? 'credit' : 'debit'}`}>
          <div className="transaction-header">
            <span>{transaction.fromAccountId === 3 ? 'Credit' : 'Debit'}</span> {/* Assuming 'fromAccountId === 3' means credit */}
            <span>{new Date(transaction.time).toLocaleDateString()} {new Date(transaction.time).toLocaleTimeString()}</span>
          </div>
          <div className="transaction-details">
            <span>Amount: {transaction.amount.toLocaleString()}</span>
            <span className="transaction-type">{transaction.mode}</span>
          </div>
          <div className="transaction-remark">
            {transaction.remark}
          </div>
        </div>
      ))}
    </div>
  );
}

export default TransactionHistory;
