import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./account.css";

function AccountDisplay() {
  const { id } = useParams();
  const [accounts, setAccounts] = useState([]);

  useEffect(() => {
    if (id) {
      fetch(`http://localhost:7000/api/getAll/${id}`)
        .then((response) => response.json())
        .then((data) => setAccounts(data))
        .catch((error) => console.error("Error fetching accounts:", error));
    }
  }, [id]);

  return (
    <div className="account-container">
      {accounts.map((account) => (
        <div className="account" key={account.accountId}>
          <div className="bank-name">銀行</div>
          <div className="account-number">Account ID: {account.accountId}</div>
          <div className="accountholder">
            {account.Type}
            <span className="balance">Balance: ₹{account.balance}</span>
          </div>
        </div>
      ))}
    </div>
  );
}

export default AccountDisplay;
