import React, { useState, useEffect } from 'react';
import './slider.css';

const randomReviews = [
  "The bank offers excellent customer service and quick support.",
  "I was able to easily apply for a loan without any hassle. Highly recommend!",
  "Their mobile banking app is very intuitive and easy to use.",
  "This bank has been my go-to for years, very trustworthy.",
  "The account management is simple, and their advisors are very helpful.",
  "I’m impressed with their investment options and returns.",
  "The branch staff are friendly and always ready to help.",
  "Best banking experience I've had, everything is straightforward.",
  "Their credit card options are fantastic with great rewards.",
  "I trust this bank with my savings. Their security is top-notch."
  ,"Excelent service", "Be"
];

const randomNames = [
  "John Doe", "Jane Smith", "Robert Johnson", "Emily Davis",
  "Michael Brown", "Laura Wilson", "James Miller", "Linda White",
  "Paul Garcia", "Anna Martinez", "Jamen Martin", "Lucid"
];

const randomImages = [
  "https://randomuser.me/api/portraits/men/1.jpg",
  "https://randomuser.me/api/portraits/women/2.jpg",
  "https://randomuser.me/api/portraits/men/3.jpg",
  "https://randomuser.me/api/portraits/women/4.jpg",
  "https://randomuser.me/api/portraits/men/5.jpg",
  "https://randomuser.me/api/portraits/women/6.jpg",
  "https://randomuser.me/api/portraits/men/7.jpg",
  "https://randomuser.me/api/portraits/women/8.jpg",
  "https://randomuser.me/api/portraits/men/9.jpg",
  "https://randomuser.me/api/portraits/women/10.jpg",
  "https://randomuser.me/api/portraits/men/11.jpg",
  "https://randomuser.me/api/portraits/women/12.jpg",
];

const testimonialsData = Array.from({ length: 12 }, (_, index) => ({
  name: randomNames[index],
  text: randomReviews[index],
  image: randomImages[index],
}));

const TestimonialsSlider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex + 4 >= testimonialsData.length ? 0 : prevIndex + 4
      );
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="testimonial-slider">
      <div className="testimonial-container">
        {testimonialsData.slice(currentIndex, currentIndex + 4).map((testimonial, index) => (
          <div key={index} className="testimonial-card">
            <img
              src={testimonial.image}
              alt={testimonial.name}
              className="testimonial-image"
            />
            <div className="testimonial-content">
              <p className="testimonial-text">{testimonial.text}</p>
              <h3 className="testimonial-name">{testimonial.name}</h3>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TestimonialsSlider;
