import React, { useState, useEffect } from "react";
import "./accounts.css";

function Accounts() {
  const [accounts, setAccounts] = useState([]);
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [userDetails, setUserDetails] = useState(null);

  // Fetch all accounts on component mount
  useEffect(() => {
    const fetchAccounts = async () => {
      try {
        const response = await fetch("http://localhost:7000/api/getAllAccounts");
        const data = await response.json();
        setAccounts(data);
      } catch (error) {
        console.error("Error fetching accounts:", error);
      }
    };

    fetchAccounts();
  }, []);

  // Fetch user details when account is selected
  const handleDetailsClick = async (userId) => {
    try {
      const response = await fetch(`http://localhost:7000/api/getUser/${userId}`);
      const data = await response.json();
      setUserDetails(data);
    } catch (error) {
      console.error("Error fetching user details:", error);
    }
  };

  // Handle account closure
  const handleCloseAccount = async (accountNo) => {
    try {
      const response = await fetch(`http://localhost:7000/api/closeAccount/${accountNo}`, {
        method: "POST",
      });

      if (response.ok) {
        alert("Account closed successfully");
        // Remove the closed account from the list
        setAccounts(accounts.filter((account) => account.accountNo !== accountNo));
      } else {
        alert("Failed to close account");
      }
    } catch (error) {
      console.error("Error closing account:", error);
    }
  };

  return (
    <div className="accounts-container">
      <h2>All Accounts</h2>
      <div className="accounts-list">
        {accounts.map((account) => (
          <div className="account-card" key={account.accountNo}>
            <p><strong>Account No:</strong> {account.accountNo}</p>
            <p><strong>Balance:</strong> {account.balance}</p>
            <button onClick={() => handleDetailsClick(account.userId)}>Details</button>
            <button className="close-btn" onClick={() => handleCloseAccount(account.accountNo)}>Close Account</button>
          </div>
        ))}
      </div>

      {/* Display user details when an account is selected */}
      {userDetails && (
        <div className="user-details">
          <h3>User Details</h3>
          <p><strong>Name:</strong> {userDetails.name}</p>
          <p><strong>Email:</strong> {userDetails.email}</p>
          <p><strong>Phone:</strong> {userDetails.phone}</p>
          <button onClick={() => setUserDetails(null)}>Close Details</button>
        </div>
      )}
    </div>
  );
}

export default Accounts;
