import React, { useState, useEffect } from 'react';
import './transaction.css';

function TransactionDisplay() {
  const [creditTransactions, setCreditTransactions] = useState([]);
  const [debitTransactions, setDebitTransactions] = useState([]);
  const userId = 3; // Assume this is the current user ID

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await fetch(`http://localhost:7000/api/payment.ginkou.in/allTransaction/${userId}`);
        const data = await response.json();
        setCreditTransactions(data.creditTransactions.sort((a, b) => new Date(b.time) - new Date(a.time)));
        setDebitTransactions(data.debitTransactions.sort((a, b) => new Date(b.time) - new Date(a.time)));
      } catch (error) {
        console.error('Error fetching transactions:', error);
      }
    };

    fetchTransactions();
  }, [userId]);

  return (
    <div className="transaction-container">
      <h2>Credit Transactions</h2>
      <div className="transaction-header">
        <span>Date</span>
        <span>Type</span>
        <span>Amount</span>
      </div>
      {creditTransactions.map((transaction) => (
        <div className="transaction-item credit" key={transaction.transactionId}>
          <div>
            <div className="transaction-date">
              {new Date(transaction.time).toLocaleDateString()}
            </div>
            <div className="transaction-remark">{transaction.remark}</div>
          </div>
          <div className="transaction-type">{transaction.mode}</div>
          <div>
            {transaction.amount.toLocaleString(undefined, { style: 'currency', currency: 'INR' })}
          </div>
        </div>
      ))}

      <h2>Debit Transactions</h2>
      <div className="transaction-header">
        <span>Date</span>
        <span>Type</span>
        <span>Amount</span>
      </div>
      {debitTransactions.map((transaction) => (
        <div className="transaction-item debit" key={transaction.transactionId}>
          <div>
            <div className="transaction-date">
              {new Date(transaction.time).toLocaleDateString()}
            </div>
            <div className="transaction-remark">{transaction.remark}</div>
          </div>
          <div className="transaction-type">{transaction.mode}</div>
          <div>
            {transaction.amount.toLocaleString(undefined, { style: 'currency', currency: 'INR' })}
          </div>
        </div>
      ))}
    </div>
  );
}

export default TransactionDisplay;
