import React, { useState, useEffect } from "react";
import "./accounts.css";

function Accounts() {
  const [accounts, setAccounts] = useState([]);
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [userDetails, setUserDetails] = useState(null);

  // Fetch all accounts on component mount
  useEffect(() => {
    const fetchAccounts = async () => {
      try {
        const response = await fetch(
          "http://localhost:7000/api/getAllAccounts"
        );
        const data = await response.json();
        setAccounts(data);
      } catch (error) {
        console.error("Error fetching accounts:", error);
      }
    };

    fetchAccounts();
  }, []);

  // Fetch user details when account is selected
  const handleDetailsClick = async (userId) => {
    try {
      const response = await fetch(
        `http://localhost:7000/api/getUser/${userId}`
      );
      const data = await response.json();
      console.log(data);
      setUserDetails(data);
    } catch (error) {
      console.error("Error fetching user details:", error);
    }
  };

  // Handle account closure
  const handleCloseAccount = async (accountNo) => {
    try {
      const response = await fetch(
        `http://localhost:7000/api/closeAccount/${accountNo}`,
        {
          method: "DELETE",
        }
      );

      if (response.ok) {
        alert("Account closed successfully");
        // Remove the closed account from the list
        setAccounts(
          accounts.filter((account) => account.accountNo !== accountNo)
        );
      } else {
        alert("Failed to close account");
      }
    } catch (error) {
      console.error("Error closing account:", error);
    }
  };

  return (
    <div className="accounts-container">
      <div className="accounts-list">
        {accounts.map((account) => (
          <div className="account-card" key={account.accountId}>
            <div className="account-info">
              <p>
                <strong>Account No:</strong> XXXX XXXX{account.accountId}
              </p>
              <div className="action-buttons">
                <button onClick={() => handleDetailsClick(account.userId)}>
                  i
                </button>
                <button
                  className="close-btn"
                  onClick={() => handleCloseAccount(account.accountId)}
                >
                  Close Account
                </button>
              </div>
            </div>
            <p>
              <strong>Balance:</strong> ₹ {account.balance}.00
            </p>
          </div>
        ))}
        
      </div>

      
    </div>
  );
}

export default Accounts;
